import { Navbar, Dropdown, Button, Nav } from "react-bootstrap";
import { NavLink, Link } from "react-router-dom";
import "./Navbar.css";
import UseHome from "../../UseForm/UseHome";
import UsePerfil from "../../UseForm/UsePerfil";
import useBarra from "../../UseForm/UseBarra";

const NavbarPage = () => {
  const { buttonSelectClick } = useBarra();
  const token = localStorage.getItem("token");
  const { usuario } = UseHome();
  const { exampleImage } = UsePerfil();
  const handleLogOut = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div className="sticky-top">
      <Navbar className="NavbarTwo">
        <Navbar.Brand as={Link} to="/">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="23"
            height="23"
            fill="currentColor"
            class="bi bi-signpost-2"
            viewBox="0 0 16 16"
          >
            <path d="M7 1.414V2H2a1 1 0 0 0-1 1v2a1 1 0 0 0 1 1h5v1H2.5a1 1 0 0 0-.8.4L.725 8.7a.5.5 0 0 0 0 .6l.975 1.3a1 1 0 0 0 .8.4H7v5h2v-5h5a1 1 0 0 0 1-1V8a1 1 0 0 0-1-1H9V6h4.5a1 1 0 0 0 .8-.4l.975-1.3a.5.5 0 0 0 0-.6L14.3 2.4a1 1 0 0 0-.8-.4H9v-.586a1 1 0 0 0-2 0zM13.5 3l.75 1-.75 1H2V3h11.5zm.5 5v2H2.5l-.75-1 .75-1H14z" />
          </svg>
          <b className="NavbarTitulo">Urban City</b>
        </Navbar.Brand>
        <Navbar.Collapse>
          <Nav className="mr-auto"></Nav>
          {!token && (
            <div className="d-flex">
              <div className="ml-2">
                <Button as={Link} to="/login" variant="outline-dark">
                  Login
                </Button>
              </div>
              <div className="ml-2">
                <Button as={Link} to="/register" variant="outline-dark">
                  Registarse
                </Button>
              </div>
            </div>
          )}
          <div>
            {token && (
              <div className="NavbarButton">
                <Dropdown onClick={buttonSelectClick}>
                  <Dropdown.Toggle>
                    <img
                      className="NavbarFoto"
                      src={usuario.imagen || exampleImage}
                      alt=""
                    />
                  </Dropdown.Toggle>
                  <Dropdown.Menu className="NavbarButtonUser">
                    <Dropdown.Item as={Link} to="/perfil">
                      Perfil
                    </Dropdown.Item>
                    <Dropdown.Item onClick={handleLogOut}>
                      Cerrar sesion
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>
            )}
          </div>
        </Navbar.Collapse>
      </Navbar>
    </div>
  );
};
export default NavbarPage;
