import { useState, useEffect } from "react";
import { getBase644, beforeUpload4 } from "../utils/index";
import UseHome from "../UseForm/UseHome";
import axios from "axios";

const UsePostPublic = () => {
  //States of Modal
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

  //States
    const { proveedor, usuario, setPublicacionActual, publicacionActual } = UseHome();
    const [validation, setValidation] = useState(false);
    const [ uploadImg, setUploadImg] = useState("")
    const token = localStorage.getItem("token");
    const [base64, setBase64] = useState("");
    const [ file, setFile ] = useState(null)
    const [input, setInput] = useState({});

  useEffect(() => {
    if(uploadImg !== null){
      console.log(uploadImg)
    }
    if(input !== 0){
      console.log(input)
    }
  },[uploadImg])

  //Funcions
  const HandleChange = (e) => {
    const { name, value } = e.target;
    const changedInput = {
      ...input,
      [name]: value,
      proveedor: proveedor,
      perfil: usuario.imagen,
      imagenPublicada: uploadImg,
    };
    setInput(changedInput);
  };
  //Codigo de imagenes
  const handlePic = (e) => {
    const pic = e.target.files[0];
    setFile(pic)
  }
  const handleUpload = () => {
    const formData = new FormData()
    formData.append('file', file)
    formData.append('upload_preset', 'wkuf5yo4')
    fetch('https://api.cloudinary.com/v1_1/five-drive/upload', {
      method: 'POST',
      body: formData,
    })
    .then(res => res.json())
    .then(res => setUploadImg(res.url))
    handleClose()
  }

  //Codigo para foto base 64
  /*const onChangeImg = async (e) => {
    const img = e.target.files[0];
    const base64img = await getBase644(img);
    const changedInput = { ...input, imagenPublicada: base64img };
    setBase64(base64img);
    setInput(changedInput);
  };*/

  const HandleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log(input)
      const headers = { "x-auth-token": token };
      await axios.post("publicacion", input, { headers });
      setPublicacionActual(true);
    } catch (error) {
      console.log(error);
      setValidation(true);
    }
  };
  const onInputClick = (event) => {
    event.target.value = "";
  };
  return {
    publicacionActual,
    onInputClick,
    HandleChange,
    HandleSubmit,
    handleUpload,
    handleClose,
    handleShow,
    validation,
    handlePic,
    base64,
    input,
    show,
  };
};
export default UsePostPublic;
